export type NotificationMessage = {
    id: string;
    source: string;
    acknowledged?: boolean;
    title?: string;
    description?: string;
    link?: string;
} & ({
    type: "acknowledge" | "confirm";
    action_label?: string;
    response?: boolean;
} | {
    type: "yesno";
    response?: boolean;
} | {
    type: "text";
    response?: string;
} | {
    type: 'passive' | 'error' | 'warning' | 'info';
});
